import React, { useState } from "react";
import { useSpring, animated } from "react-spring";
import Signin from './Sing';
import Signup from './Signup';
import "./signin.css"
  

function SigninRegister() {
    const [registrationForm, setRegistrationForm] = useState(false);
    const loginproperties = useSpring({
        left: registrationForm ? -500 : 0,
        // opacity: registrationForm ? 0 : 1

    }) 

    const registerproperties = useSpring({
        left: registrationForm ? 0 : 500,
        // opacity: registrationForm ? 1 : 0

    }) 

    const loginBtnproperties = useSpring({
        borderBottom: registrationForm 
         ? 'solid 0px transparent'
         :"solid 2px blue", 
        })

    const registerBtnproperties = useSpring({
         borderBottom: registrationForm 
        ? "solid 2px blue" :
         'solid 0px transparent',
         })
    

    function signupClicked(){
        setRegistrationForm(true)
    }
    function signinClicked(){
        setRegistrationForm(false)
    }
    


    return(
    <div className="login-container">
        <div className ="login-register-wrapper">
            <div className="nav-buttons">
                <animated.button onClick={signinClicked} 
                id="loginBtn" style={loginBtnproperties} >
                    Login
                </animated.button>

                <animated.button onClick={signupClicked} id="registerBtn" 
                style={registerBtnproperties}>
                    Register
                </animated.button>
            </div>
         <div className="form-group">
            <animated.form action='' id='loginform' style={loginproperties}> <Signin/></animated.form>
            <animated.form action='' id='registerform' style={registerproperties}> <Signup/></animated.form>
        </div>
        <animated.form className="forgot-panel" > 
            <a href="#"> Forgot your password?</a>
        </animated.form>
   </div>
</div>
    )
}
export default SigninRegister;
