import React from 'react'
import Signin from './Sing'
import Signup from './Signup'
import {useSpring, animated} from 'react-spring'
import {useState} from 'react';
import "./signin.css"

function SigninRegister(){
    const [registrationForm, setRegistrationForm] = useState(false);
    const loginproperties = useSpring({
        left: registrationForm ? -430 : 0

    }) 
    function signupClicked(){
        setRegistrationForm(true)
    }
    function signinClicked(){
        setRegistrationForm(false)
    }
    


    return(
    <div className="login-container">
        <div className ="login-register-wrapper">
            <div className="nav-buttons">
                <button onClick={signinClicked} id="loginBtn">Login</button>
                <button onClick={signupClicked} id="registerBtn">Register</button>
            </div>
         <div className="form-group">
            <animated.form action='' id='loginform'> <Signin/></animated.form>
            <animated.form action='' id='registerform'> <Signup/></animated.form>
        </div>
        <div className="forgot-panel" > 
            <a href="#"> Forgot your password?</a>
        </div>
   </div>
</div>
    )
}
export default SigninRegister;
