import { useState, useEffect} from "react";



function Validation (){
    const [values, setValues] = useState({
        username: '',
        email: '',
        password: '',
        password2: '',
    })
    const [errors, setErrors] = useState({})

    const handleChange = e =>{
        const {name, values} = e.target
        setValues({
            ...value,
            [name]: value
        });
    };
    
    return { handleChange, values };


}
export default Validation ;
